import React, { Component } from 'react';
import axios from 'axios';
import './config';

const serverUrl = 'http://103.120.10.57:5000';
const http = axios.create({
    baseURL: serverUrl
});

class Login extends Component {

    constructor() {
        super();
        this.state = {
            name: "",
            password: ""
        }
        this.handleChange = this.handleChange.bind(this)
    }

    handleChange(event) {
        const {name, value} = event.target
        this.setState({
            [name]: value
        })
    }

    //handleSubmit(event) {
    //    //user = this.state.name;
    //    console.log("fdsfdsfdsa");
    //    console.log(this.state.name);
    //    alert("Fdsafdsfdsa")
    //    event.preventDefault();
    //
    //    //if (!user) {
    //    //    http.post('/login', {username})
    //    //}
    //}

    onSubmit(e) {
        var _this = this
        e.preventDefault(); // Prevent default so it doesn't refresh
        console.log(this.state.name);
        console.log(this.state.password);
        //var user = this.state.name;
        //if (user) {
        //    //http.post('/login', {user})
        //    fetch('/login', {
        //        method: 'POST',
        //        body: user,
        //    });
        //}

        axios.post('http://localhost:2000/login', {
                user: this.state.name,
                password: this.state.password
            })
            .then(function (response) {
                var __this = _this;
                console.log("返回了什么")
                console.log(response);
                //__this.props.onSendMessage(response.data, "Bot");
                global.constants.token = response.data.token
                console.log(global.constants.token)
                alert('You hvae successfully log in, now you can start to talk with Bot')
            })
            .catch(function (error) {
                var __this = _this;
                console.log("什么错误");
                console.log(error);
                __this.props.onSendMessage("The system is under maintenance.....", "Bot");
            });
    }


    //onLogin: function() {
    //    user = this.state.username;
    //    if (!user) {
    //        http.post('/login', {username})
    //        .then(() => this.setState({isLoggedIn: true}))
    //        .catch((err) => console.log(err));
    //    }
    //}

    render() {
        const {messages} = this.props;
        return (
            <div className="Input">
                <form class='header item' onSubmit={e => this.onSubmit(e)}>
                    <input type='text'
                           value={this.state.name}
                           name='name'
                           placeholder='name'
                           onChange={ this.handleChange }
                    />
                    &nbsp
                    <input type='password'
                           value={this.state.password}
                           name='password'
                           placeholder='password'
                           onChange={ this.handleChange }
                    />
                    <button>Login</button>
                    <span>{this.state.name}</span>
                    <span>{this.state.password}</span>
                </form>

            </div>

        );
    }
}

export default Login;
